import os
from pathlib import Path

def getResource(request):
    """
    Get username and date from the request, then
    send the file to the user by creating path by the username and date.
    """
    # Step 1: Get username and date from the request
    username = request.GET.get('username')
    date = request.GET.get('date')

    # Step 2: Validate that we have both parameters
    if not username or not date:
        return HttpResponse("Missing username or date parameter", status=400)

    # Step 3: Validate inputs to prevent path traversal attacks
    # Remove any path traversal attempts and validate characters
    if not username.replace('_', '').replace('-', '').isalnum():
        return HttpResponse("Invalid username format", status=400)

    if not date.replace('-', '').replace('_', '').isalnum():
        return HttpResponse("Invalid date format", status=400)

    # Check for path traversal attempts
    if '..' in username or '/' in username or '\\' in username:
        return HttpResponse("Invalid username", status=400)

    if '..' in date or '/' in date or '\\' in date:
        return HttpResponse("Invalid date", status=400)

    # Step 4: Create the file path using username and date with Path for safer handling
    base_dir = Path('resources').resolve()
    file_path = (base_dir / username / date).resolve()

    # Step 5: Ensure the resolved path is still within the base directory (prevent path traversal)
    try:
        file_path.relative_to(base_dir)
    except ValueError:
        return HttpResponse("Invalid path", status=400)

    # Step 6: Check if the file/path exists
    if not file_path.exists():
        raise Http404("Resource not found")

    # Step 7: Ensure it's a file, not a directory
