from flask import request, redirect

def flask_redirect():
    """Get the url from the request and redirect to it"""
    # Get the URL from the request query parameters
    # Common parameter names are 'url', 'next', or 'redirect'
    url = request.args.get('url') or request.args.get('next') or request.args.get('redirect')

    # If no URL is found in query parameters, try form data
    if not url:
        url = request.form.get('url') or request.form.get('next') or request.form.get('redirect')

    # If we have a URL, redirect to it
    if url:
        return redirect(url)

    # If no URL is provided, redirect to root as fallback
    return redirect('/')
