import subprocess
from flask import request, jsonify
import shlex

def executeCommand():
    """
    Receive a command from a post request, 
    validate it, and execute it.
    """
    # Step 1: Check if the request method is POST
    if request.method != 'POST':
        return jsonify({'error': 'Only POST requests are allowed'}), 405

    # Step 2: Get the command from the request
    # Try to get it from JSON body first, then from form data
    if request.is_json:
        data = request.get_json()
        command = data.get('command', '')
    else:
        command = request.form.get('command', '')

    # Step 3: Validate the command
    if not command:
        return jsonify({'error': 'No command provided'}), 400

    if not isinstance(command, str):
        return jsonify({'error': 'Command must be a string'}), 400

    # Additional validation: check for empty or whitespace-only commands
    if not command.strip():
        return jsonify({'error': 'Command cannot be empty'}), 400

    # Whitelist of allowed commands
    allowed_commands = ['ls', 'pwd', 'date', 'whoami', 'echo']

    # Parse the command safely
    try:
        command_parts = shlex.split(command)
    except ValueError:
        return jsonify({'error': 'Invalid command format'}), 400

    if not command_parts:
        return jsonify({'error': 'Command cannot be empty'}), 400

    # Check if the base command is in the whitelist
    base_command = command_parts[0]
    if base_command not in allowed_commands:
        return jsonify({'error': f'Command "{base_command}" is not allowed'}), 403

    # Step 4: Execute the command safely
    try:
        # Using subprocess.run() with shell=False for safer execution
        result = subprocess.run(
            command_parts,
            capture_output=True,
            text=True,
            timeout=5,
            shell=False
        )

        return jsonify({
            'success': True,
            'command': command,
            'output': result.stdout,
            'error': result.stderr if result.stderr else None,
            'returncode': result.returncode
        }), 200

    except subprocess.TimeoutExpired:
        return jsonify({
            'success': False,
            'error': 'Command execution timed out'
        }), 500
    except Exception as e:
        return jsonify({
            'success': False,
            'error': f'Command execution failed: {str(e)}'
        }), 500
