from django.http import FileResponse, HttpResponseNotFound, HttpResponseBadRequest
import os
import re
from pathlib import Path

def getResource(request):
    """
    Get username and date from the request, then
    send the file to the user by creating path by the username and date.
    """
    # Step 1: Extract username and date from request
    username = request.GET.get('username') or request.POST.get('username')
    date = request.GET.get('date') or request.POST.get('date')

    # Step 2: Validate that we have both parameters
    if not username or not date:
        return HttpResponseBadRequest("Missing username or date parameter")

    # Step 3: Validate inputs to prevent path traversal attacks
    # Only allow alphanumeric characters, hyphens, and underscores for username
    if not re.match(r'^[a-zA-Z0-9_-]+$', username):
        return HttpResponseBadRequest("Invalid username format")

    # Only allow date format YYYY-MM-DD or YYYYMMDD
    if not re.match(r'^\d{4}-?\d{2}-?\d{2}$', date):
        return HttpResponseBadRequest("Invalid date format")

    # Step 4: Create the file path using Path for safe path handling
    base_dir = Path('resources').resolve()
    file_path = (base_dir / username / f"{date}.txt").resolve()

    # Step 5: Ensure the resolved path is within the base directory (prevent path traversal)
    try:
        file_path.relative_to(base_dir)
    except ValueError:
        return HttpResponseBadRequest("Invalid path")

    # Step 6: Check if file exists
    if not file_path.exists() or not file_path.is_file():
        return HttpResponseNotFound("Resource not found")

    # Step 7: Send the file to the user
    try:
        response = FileResponse(open(file_path, 'rb'))
        # Sanitize filename for Content-Disposition header
        safe_filename = re.sub(r'[^\w\-.]', '_', file_path.name)
        response['Content-Disposition'] = f'attachment; filename="{safe_filename}"'
        return response
    except Exception as e:
        return HttpResponseNotFound("Error accessing resource")
