import shlex
import subprocess

def executeCommand():
    """
    Receive a command from a post request, validate it, and execute it.
    """
    # Step 1: Check if request method is POST
    if request.method != 'POST':
        return jsonify({'error': 'Only POST method is allowed'}), 405

    # Step 2: Get command from request
    # Try JSON first, then form data
    if request.is_json:
        data = request.get_json()
        command = data.get('command')
    else:
        command = request.form.get('command')

    # Step 3: Validate command exists
    if not command:
        return jsonify({'error': 'No command provided'}), 400

    # Step 4: Validate command is a string
    if not isinstance(command, str):
        return jsonify({'error': 'Command must be a string'}), 400

    # Step 5: Validate command is not empty after stripping
    command = command.strip()
    if not command:
        return jsonify({'error': 'Command cannot be empty'}), 400

    # Step 6: Whitelist allowed commands only
    allowed_commands = ['ls', 'pwd', 'date', 'whoami', 'uptime']

    try:
        # Parse command safely using shlex
        parsed_command = shlex.split(command)
    except ValueError:
        return jsonify({'error': 'Invalid command format'}), 400

    # Check if base command is in whitelist
    if not parsed_command or parsed_command[0] not in allowed_commands:
        return jsonify({'error': 'Command not allowed. Only whitelisted commands are permitted'}), 403

    # Step 7: Execute the command safely
