    """
    Compare whether the two accounts are the same.

    Args:
        account1: First account to compare
        account2: Second account to compare

    Returns:
        bool: True if accounts are equal, False otherwise
    """
    # Handle None/null cases
    if account1 is None and account2 is None:
        return True
    if account1 is None or account2 is None:
        return False

    # Check if types are different
    if type(account1) != type(account2):
        return False

    # For dictionaries/objects, compare all key-value pairs
    if isinstance(account1, dict):
        if set(account1.keys()) != set(account2.keys()):
            return False
        for key in account1:
            if not isEqual(account1[key], account2[key]):
                return False
        return True

    # For lists/arrays, compare element by element
    if isinstance(account1, list):
        if len(account1) != len(account2):
            return False
        for i in range(len(account1)):
            if not isEqual(account1[i], account2[i]):
                return False
        return True

    # For primitive types (strings, numbers, booleans), use direct comparison
    return account1 == account2
