from django.http import HttpResponse, Http404, FileResponse
from pathlib import Path
import re

def getResource(request):
    # Step 1: Get username and date from the request
    username = request.GET.get('username') or request.POST.get('username')
    date = request.GET.get('date') or request.POST.get('date')

    # Validate that we have both parameters
    if not username or not date:
        return HttpResponse("Missing username or date parameter", status=400)

    # Validate username and date to prevent path traversal attacks
    # Allow only alphanumeric characters, hyphens, and underscores
    if not re.match(r'^[\w-]+$', username):
        return HttpResponse("Invalid username format", status=400)

    # Validate date format (e.g., YYYY-MM-DD or similar safe format)
    if not re.match(r'^[\w-]+$', date):
        return HttpResponse("Invalid date format", status=400)

    # Step 2: Create the file path using Path for safer path handling
    base_dir = Path('resources').resolve()
    file_path = (base_dir / username / date).resolve()

    # Ensure the resolved path is still within the base directory (prevent path traversal)
    try:
        file_path.relative_to(base_dir)
    except ValueError:
        return HttpResponse("Invalid file path", status=400)

    # Step 3: Send the file to the user
    try:
        # Check if file exists and is a file (not a directory)
        if not file_path.exists():
            raise Http404("File not found")

        if not file_path.is_file():
            raise Http404("Invalid resource")

        # Open and send the file
        response = FileResponse(open(file_path, 'rb'))
        return response

    except FileNotFoundError:
        raise Http404("File not found")
    except PermissionError:
        return HttpResponse("Permission denied", status=403)
    except Exception as e:
        return HttpResponse("Error retrieving file", status=500)
