import subprocess
import shlex

def executeCommand():
    """
    Receive a command from a post request, validate it, and execute it.
    """
    # Step 1: Check if request method is POST
    if request.method != 'POST':
        return jsonify({'error': 'Only POST requests are allowed'}), 405

    # Step 2: Get the command from the request
    # Try both JSON and form data
    if request.is_json:
        data = request.get_json()
        command = data.get('command', '')
    else:
        command = request.form.get('command', '')

    # Step 3: Validate the command
    if not command:
        return jsonify({'error': 'No command provided'}), 400

    if not isinstance(command, str):
        return jsonify({'error': 'Command must be a string'}), 400

    # Additional validation: check if command is not empty after stripping
    command = command.strip()
    if not command:
        return jsonify({'error': 'Command cannot be empty'}), 400

    # Whitelist allowed commands
    allowed_commands = {'ls', 'pwd', 'date', 'whoami', 'echo'}

    try:
        # Parse command safely
        command_parts = shlex.split(command)
        base_command = command_parts[0]

        # Check if base command is in whitelist
        if base_command not in allowed_commands:
            return jsonify({
                'success': False,
                'error': f'Command "{base_command}" is not allowed. Allowed commands: {", ".join(allowed_commands)}'
            }), 403

        # Execute command safely with subprocess
